(561, 162, '_form', '<div class="form-sponsorship">\n<h3>LEARN MORE:</h3>\n<p>Use the form below to request our media kit and \na free needs assessment</p>\n<div class="input-group">\n[text* your-name placeholder "Your name *"]\n</div>\n<div class="input-group">\n[text* title placeholder "Title *"]\n</div>\n<div class="input-group">\n[email* your-email placeholder "Your email *"]\n</div>\n<div class="input-group">\n[text* company placeholder "Company *"]\n</div>\n<div class="input-group">\n[text* phone placeholder "Your phone number *"]\n</div>\n<div class="input-group your-message">\n[textarea your-message placeholder "I have the following questions:"]\n</div>\n<span class="text-required">*required fields</span>\n<div class="input-check">[checkbox* checkbox-799 "Yes, I would like a complete set of information about RetailWire marketing and sponsorship programs"]</div>	\n<div class="btn-submit">[submit "SUBMIT"]</p>\n</div>'),
